/* simple clean */
*{
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}


html, body{
  height: 100%;
  width: 100%;
  overflow: hidden;
  /* background: #303346; */
}

body{
  font-family: 'helveticaNeue', Arial;
  font-size: 12px;
  color: #fff;
}

.page{
  height: 100%;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  /* background: #303346; */
}

/* areas */
.area{
  transition: all 0.25s ease-in-out
}
.area-01{
  background: #303346;
  width: 50%;
  height: 33.45%;
}
.area-02{
  background: #d1c7b2;
  width: 50%;
  height: 66.6%;
}
.area-03{
  position: relative;
  width: 100%;
  height: 33.4%;
  color: #303346;
}
.title{
  font-size: 108px;
  font-family: 'helveticaNeue Bold', Arial;
  transition: all 0.25s ease-in-out;
  position: relative;
  left: -2.25%;
  line-height: 0.775;
}
.text-box{
  position: absolute;
  left: 9vw;
  bottom: 6vh;
}
.golden-copy{
  margin-bottom: 10px;
}
.text{
  display: inline-block;
  position: absolute;
  transition: all 0.25s ease-in-out
}
.text-01{
  left: 9vw;
  transform: rotate(90deg) translateY(-100%);
  transform-origin: 0% 0%;
  top: 5.4vh;
}
.text-02{ 
  top: 5.2vh;
  left: 34vw;
  width: 85px;
}
.text-03{
  transform: rotate(90deg);
  transform-origin: 0% 100%;
  top: 32.5vh;
  left: 87.5vw;
  display: block;
  white-space: nowrap;
}
@media (min-width:768px) {
  .area-01{
    height: 35%;
  }
  .title{
    font-size: 151px;
  }
  .text-box{
    left: 12.6vw;
    bottom: 8.3vh;
  }
  .golden-copy{
    margin-bottom: 17px;
  }
  .text-01{
    left: 8.3vw;
    top: 5.3vh;
  }
  .text-02{ 
    left: 37vw;
    top: 5.3vh;
    width: auto;
  }
  .text-03{
    left: 93.75vw;
  }
}
@media (min-width:1024px) {
  .area-01{
    width: 25%;
    height: 50%;
  }
  .area-02{
    width: 75%;
    height: 69.3%;
  }
  .area-03{
    height: 30.7%;
  }
  .title{
    font-size: 283px;
  }
  .text-box{
    left: 6vw;
    bottom: 6vh;
  }
  .golden-copy{
    margin-bottom: 13px;
  }
  .text-01{
    left: 6vw;
    top: 5.2vh;     
  }
  .text-02{ 
    top: 5.2vh;
    left: 20vw;
  }
  .text-03{
    top: inherit;
    bottom: 50vh;
    left: inherit;
    right: 6vw;
    transform-origin: 100% 0%;
  }
}
